class Block {
  constructor(routeCallback) {
    this.html = [];
    this.routeCallback = routeCallback;
  }

  toString() {
    return this.html.join('\n');
  }

  append(more) {
    this.html.push(more);
  }

  prepend(more) {
    this.html.unshift(more);
  }

  replace(instead) {
    this.html = [ instead ];
  }

  defineRoutes() {
    this.routeCallback();
  }
}

module.exports = Block;
